<?phpnamespace Nemundo\Html\Header\Style;use Nemundo\Html\Container\AbstractHtmlContainer;// CssStyleclass Style extends AbstractHtmlContainer{    public function addContent($content)    {        return parent::addContent($content);    }    public function addStyle($style)    {        $this->addContent($style);        return $this;    }    public function getContent()    {        $this->tagName = 'style';        return parent::getContent();    }}