<?phpnamespace Nemundo\Html\Script;use Nemundo\Html\Header\AbstractHeaderHtmlContainer;abstract class AbstractJavaScript extends AbstractHeaderHtmlContainer{    /**     * @var string     */    protected $src;    protected $type = 'text/javascript';    /**     * @var bool     */    protected $defer = false;    /**     * @var bool     */    protected $async = false;    // addLine    // addHtml    // addCode    protected function addCodeLine($line)    {        $this->addContent($line);        return $this;    }    public function getContent()    {        $this->tagName = 'script';        $this->addAttribute('type', $this->type);        $this->addAttribute('src', $this->src);        if ($this->defer) {            $this->addAttributeWithoutValue('defer');        }        if ($this->async) {            $this->addAttributeWithoutValue('async');        }        return parent::getContent();    }}